mapper.insert:

INSERT INTO chatroom(
	roomId,
    TITLE
	)
VALUES(
	#{roomId},
	#{title}
	)


INSERT INTO ChatMemberList(
	roomId,
    memberId
	)
VALUES(
	#{roomId},
	#{memberId}
	)


INSERT INTO ChatMemberList(
	roomId,
    memberId
	)
VALUES(
	#{roomId},
	#{memberId}

	)

mapper.select:

 SELECT COUNT(*) FROM
  						CHATMEMBERLIST
						WHERE ROOMID =#{roomId} and memberId=#{memberId};
