mapper.insert:

INSERT INTO MEMBERLIST  (
                  MEMBER_NO,
                  MEMBER_ID,
                  DETAIL_NO
              )
         VALUE (
                  #{memberNo},
                  #{memberId},
                  #{detailNo}
               )

mapper.delete:

    DELETE FROM MEMBERLIST
WHERE 
   DETAIL_NO =#{detailNo}
AND 
   MEMBER_ID =#{memberId}

mapper.select:

 SELECT * FROM BOARD
WHERE SEQ IN (   
            SELECT DETAIL_NO
               FROM BOARDLIKE
               WHERE MEMBER_ID = #{parId}
         )
